    }
    render() { 
        let classes = this.newMethod();
        return(
        <React.Fragment>
            {/* <img src={this.state.imageUrl} alt=''></img> */}
            <span className={this.newMethod()}>{this.state.count}</span>
            <button onClick={this.handleClick} style={{fontSize:50}} className='btn btn-outline-secondary btn-sm'>hellloooooo</button>
            <div>{this.renderTags()}</div>
        </React.Fragment>) 

    }

    newMethod() {
        let classes = 'badge m-c badge-';
        classes += this.state.count === 0 ? 'warning' : 'primary';
        return classes;
    }
}

export default Counter;
